# ==============================================================================
# STANDARD AND EXTERNAL IMPORTS
# ==============================================================================
import http.server
import socketserver
import socket
import os
import sys
import ipaddress
import threading
import csv
from typing import Optional, List

import customtkinter as ctk
from tkinter import messagebox, filedialog
from PIL import Image
import io
import base64

# ==============================================================================
# GLOBAL CONFIGURATION AND APPEARANCE
# ==============================================================================
ctk.set_appearance_mode("dark")
ctk.set_default_color_theme("dark-blue")

DEFAULT_PORT = 8000

# ==============================================================================
# CUSTOM HTTP REQUEST HANDLER
# ==============================================================================
class CustomHTTPRequestHandler(http.server.SimpleHTTPRequestHandler):
    """HTTP handler that supports injecting a log callback function."""
    log_callback: Optional[callable] = None

    def log_message(self, format_str: str, *args) -> None:
        message = f"{self.client_address[0]} - {format_str % args}"
        if self.log_callback:
            self.log_callback(message)

# ==============================================================================
# MAIN APPLICATION (GUI + SERVER LOGIC)
# ==============================================================================
class WebServerApp:
    def __init__(self, root: ctk.CTk) -> None:
        self.root = root
        self.server: Optional[socketserver.TCPServer] = None
        self.server_thread: Optional[threading.Thread] = None
        self.local_ips: List[str] = []

        self._configure_window()
        self._change_to_script_directory()
        self.local_ips = self._get_local_ips()
        self._create_widgets()
        self._update_buttons(active=False)

    # -------------------------------------------------------------------------
    # MAIN WINDOW CONFIGURATION
    # -------------------------------------------------------------------------
    def _configure_window(self) -> None:
        self.root.title("Local Web Server - MA de Pablo")
        self.root.geometry("600x520")
        self.root.minsize(600, 500)
        self.root.protocol("WM_DELETE_WINDOW", self._on_close)

    def _change_to_script_directory(self) -> None:
        """Change working directory to the script's location."""
        script_dir = os.path.dirname(os.path.abspath(sys.argv[0]))
        os.chdir(script_dir)

    # -------------------------------------------------------------------------
    # NETWORK AND SYSTEM UTILITIES
    # -------------------------------------------------------------------------
    def _get_local_ips(self) -> List[str]:
        """Get all local IPv4 addresses (excluding loopback)."""
        host = socket.gethostname()
        ips = set()
        try:
            for info in socket.getaddrinfo(host, None):
                ip = info[4][0]
                if ipaddress.ip_address(ip).version == 4 and not ip.startswith("127."):
                    ips.add(ip)
        except Exception:
            pass
        return sorted(ips, key=lambda x: ipaddress.IPv4Address(x))

    # -------------------------------------------------------------------------
    # WIDGET CREATION AND GUI MANAGEMENT
    # -------------------------------------------------------------------------
    def _create_widgets(self) -> None:
        # Logo (optional, base64-encoded)
        try:
            image_b64 = ""
            image_data = base64.b64decode(image_b64)
            image = Image.open(io.BytesIO(image_data))
            self.logo_image = ctk.CTkImage(light_image=image, dark_image=image, size=(70, 50))
            self.logo_label = ctk.CTkLabel(self.root, image=self.logo_image, text="")
            self.logo_label.place(x=26, y=18)
        except Exception:
            pass  # Skip logo if loading fails

        # Main title
        ctk.CTkLabel(self.root, text="LOCAL WEB SERVER ", font=("Segoe UI", 24, "bold")).pack(pady=(15, 10))

        # Local IPs
        ctk.CTkLabel(self.root, text="Local IP addresses:", font=("Segoe UI", 14, "bold")).pack(pady=(10, 0))
        frame_ips = ctk.CTkFrame(self.root, fg_color="transparent")
        frame_ips.pack(pady=5)
        self.ips_textbox = ctk.CTkTextbox(frame_ips, height=60, width=450, font=("Consolas", 12), wrap="none")
        self.ips_textbox.pack(side="left", fill="both", expand=True)
        self.ips_textbox.configure(state="disabled")

        # Port
        ctk.CTkLabel(self.root, text="Port:", font=("Segoe UI", 14, "bold")).pack(pady=(10, 0))
        self.port_entry = ctk.CTkEntry(self.root, width=100, justify="center")
        self.port_entry.insert(0, str(DEFAULT_PORT))
        self.port_entry.pack(pady=5)
        self.port_entry.bind("<KeyRelease>", self._port_modified)

        # Shared folder
        frame_title_with_button = ctk.CTkFrame(self.root, fg_color="transparent")
        frame_title_with_button.pack(pady=(10, 0))
        ctk.CTkLabel(frame_title_with_button, text="Shared folder:", font=("Segoe UI", 14, "bold")).pack(side="left")
        self.folder_button = ctk.CTkButton(
            frame_title_with_button, text="", width=30, font=("Segoe UI", 14), command=self._select_new_folder
        )
        self.folder_button.pack(side="left", padx=(15, 0))
        current_path = os.getcwd()
        self.path_entry = ctk.CTkEntry(self.root, width=500, justify="center")
        self.path_entry.insert(0, f" {current_path}")
        self.path_entry.configure(state="disabled")
        self.path_entry.pack(pady=5)

        # Control buttons
        frame_buttons = ctk.CTkFrame(self.root, fg_color="transparent")
        frame_buttons.pack(pady=15)
        self.start_button = ctk.CTkButton(frame_buttons, text=" Start Server", command=self._start_server)
        self.start_button.pack(side="left", padx=10)
        self.stop_button = ctk.CTkButton(frame_buttons, text=" Stop Server", command=self._stop_server)
        self.stop_button.pack(side="left", padx=10)

        # Operation log
        frame_log_title = ctk.CTkFrame(self.root, fg_color="transparent")
        frame_log_title.pack(fill="x", padx=20, pady=(10, 0))
        ctk.CTkLabel(frame_log_title, text="Operation log:", font=("Segoe UI", 14, "bold")).pack(side="left")
        self.save_log_button = ctk.CTkButton(
            frame_log_title, text="", width=30, font=("Segoe UI", 14), command=self._save_log
        )
        self.save_log_button.pack(side="right")
        frame_log = ctk.CTkFrame(self.root, fg_color="transparent")
        frame_log.pack(fill="both", expand=True, padx=20, pady=(5, 15))
        self.log_textbox = ctk.CTkTextbox(frame_log, height=100, font=("Consolas", 12))
        self.log_textbox.pack(fill="both", expand=True)
        self.log_textbox.configure(state="disabled")

        self._update_display()

    def _update_display(self) -> None:
        """Update the IP address textbox with accessible server URLs."""
        self.ips_textbox.configure(state="normal")
        self.ips_textbox.delete("0.0", "end")
        if self.local_ips:
            for ip in self.local_ips:
                self.ips_textbox.insert("end", f"   http://{ip}:{self._get_port()}\n")
        else:
            self.ips_textbox.insert("end", "   No IPv4 addresses found\n")
        self.ips_textbox.configure(state="disabled")

    def _port_modified(self, event=None) -> None:
        """Update displayed URLs if the entered port is valid."""
        try:
            int(self.port_entry.get())
            self._update_display()
        except ValueError:
            pass

    def _get_port(self) -> int:
        """Return validated port (165535) or default if invalid."""
        try:
            port = int(self.port_entry.get())
            if 1 <= port <= 65535:
                return port
        except ValueError:
            pass
        return DEFAULT_PORT

    # -------------------------------------------------------------------------
    # HTTP SERVER MANAGEMENT
    # -------------------------------------------------------------------------
    def _start_server(self) -> None:
        port = self._get_port()
        if port != int(self.port_entry.get()):
            self.port_entry.delete(0, "end")
            self.port_entry.insert(0, str(port))

        try:
            handler = CustomHTTPRequestHandler
            handler.log_callback = self._log
            self.server = socketserver.TCPServer(("", port), handler)
            self.server.allow_reuse_address = True
            self.server_thread = threading.Thread(target=self.server.serve_forever, daemon=True)
            self.server_thread.start()
            self.port_entry.configure(state="disabled")
            self._update_buttons(active=True)
            self._log(f" Server started on port {port}")
        except OSError as e:
            msg = str(e).lower()
            if e.errno == 48 or "address already in use" in msg:
                self._log(f" Port {port} is already in use.")
                messagebox.showerror("Error", f"Port {port} is already in use.")
            else:
                self._log(f" Failed to start server: {e}")
                messagebox.showerror("Error", f"Could not start server:\n{e}")
        except Exception as e:
            self._log(f" Unexpected error: {e}")
            messagebox.showerror("Error", f"Unexpected error:\n{e}")

    def _stop_server(self) -> None:
        if self.server:
            self.server.shutdown()
            self.server.server_close()
            self.server = None
        self.port_entry.configure(state="normal")
        self._update_buttons(active=False)
        self._log(" Server stopped.")

    def _log(self, message: str) -> None:
        """Log a message with timestamp."""
        import time
        self.log_textbox.configure(state="normal")
        self.log_textbox.insert("end", f"[{time.strftime('%H:%M:%S')}] {message}\n")
        self.log_textbox.see("end")
        self.log_textbox.configure(state="disabled")

    def _save_log(self) -> None:
        """Save log content to a CSV file."""
        content = self.log_textbox.get("0.0", "end").strip()
        if not content:
            messagebox.showinfo("Info", "The log is empty.")
            return
        file_path = filedialog.asksaveasfilename(
            title="Save log as",
            defaultextension=".csv",
            filetypes=[("CSV Files", "*.csv"), ("All Files", "*.*")]
        )
        if not file_path:
            return
        try:
            with open(file_path, mode='w', encoding='utf-8', newline='') as f:
                writer = csv.writer(f)
                writer.writerow(["Time", "Event"])
                for line in content.splitlines():
                    if line.startswith("[") and "] " in line:
                        parts = line.split("] ", 1)
                        if len(parts) == 2:
                            timestamp_str = parts[0][1:]
                            event = parts[1]
                            writer.writerow([timestamp_str, event])
                        else:
                            writer.writerow(["", line])
                    else:
                        writer.writerow(["", line])
            messagebox.showinfo("Success", f"Log saved to:\n{file_path}")
            self._log(" Log exported to CSV.")
        except Exception as e:
            messagebox.showerror("Error", f"Could not save file:\n{e}")
            self._log(f" Export error: {e}")

    def _select_new_folder(self) -> None:
        """Allow user to change the shared folder (only if server is stopped)."""
        if self.server is not None:
            messagebox.showwarning("Operation not allowed", "Stop the server before changing the shared folder.")
            return
        current_dir = self.path_entry.get().replace(" ", "").strip()
        if not os.path.isdir(current_dir):
            current_dir = os.path.expanduser("~")
        new_dir = filedialog.askdirectory(title="Select shared folder", initialdir=current_dir)
        if new_dir:
            os.chdir(new_dir)
            self.path_entry.configure(state="normal")
            self.path_entry.delete(0, "end")
            self.path_entry.insert(0, f" {new_dir}")
            self.path_entry.configure(state="disabled")
            self._log(f" Shared folder changed to: {new_dir}")

    def _update_buttons(self, active: bool) -> None:
        """Enable/disable buttons based on server state."""
        if active:
            self.start_button.configure(state="disabled")
            self.stop_button.configure(state="normal")
        else:
            self.start_button.configure(state="normal")
            self.stop_button.configure(state="disabled")

    def _on_close(self) -> None:
        """Stop server when window is closed."""
        self._stop_server()
        self.root.destroy()

# ==============================================================================
# PROGRAM ENTRY POINT
# ==============================================================================
if __name__ == "__main__":
    root = ctk.CTk()
    app = WebServerApp(root)
    root.mainloop()